sys_script_include.script:
var GetDepart = Class.create();
GetDepart.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getDepart: function(){
		var id = this.getParameter('sysparm_sys_id');
		var getDepart = new GlideRecord('sys_user');
		getDepart.addQuery('sys_id', '=', id);
		getDepart.query();

		if (getDepart.next()){
			return getDepart.department.name + '';
		}
	},
    type: 'GetDepart'
});
